import React, { Component } from 'react'
import './App.css'
import Header from './component/Header'
import List from './component/List'
import Footer from './component/Footer'

export default class App extends Component {
  state = { todos: [{ id: '001', text: '有氧运动', done: true }, 
    { id: '002', text: '阅读', done: false },
    {id:'003',text:'学习axaj',done:false},
    {id:'004',text:'领取快递',done:false}] }
  //根据item里面每一个todo的属性修改状态
    updateTodo=(id,do_or_not)=>{
      const{todos}=this.state;
      const newTodos=todos.map((todos)=>{
        if(todos.id!==id){return todos}else{
          done=do_or_not
        }
      })

    }

  render() {
    const {todos}=this.state;

    return (
      
  
      <div className="todo-container">
        <div className="todo-wrap">
          <Header />
          <List todos={todos} updateTodo={this.updateTodo} />
          <Footer />
        </div>
      </div>
    )
  }
}
