import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {

    state = { mouse: false }
    //鼠标移入移出事件
    mouseMove=(flag)=>{

    }


    //根据item里面的todo值修改更新状态
    passTodo = (id) => {
        return (event) => {
            this.props.updateTodo(id, event.target.checked)
        }

    }

    render() {
        const { id, text, done } = this.props
        return (
            <li style={{ backgroundColor: "#ddd" }} onMouseEnter={this.mouseMove(true) }onmouseleave={this.mouseMove(false)}>
                <label>
                    <input type="checkbox" onChanged={this.passTodo(id)} checked={done} />
                    <span>{text}</span>
                </label>
                <button className="btn btn-danger" style={{ display: 'none' }}>删除</button>
            </li>
        )
    }
}
